-- ----------------------------
-- Table structure for diary
-- ----------------------------
DROP TABLE IF EXISTS `diary`;
CREATE TABLE `diary` (
  `id` int(60) NOT NULL COMMENT '主键id',
  `title` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '标题',
  `abs` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '摘要',
  `place` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '地点/位置',
  `cover` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '封面',
  `content` mediumtext CHARACTER SET utf8mb4 COMMENT '内容',
  `watch` int(11) DEFAULT '0' COMMENT '浏览人数',
  `classify` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '分类',
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  `goTime` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '出发时间',
  `day` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '旅游天数',
  `money` double DEFAULT NULL COMMENT '花费金额',
  `accompany` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '陪同人员',
  `writeTime` datetime DEFAULT NULL COMMENT '写作时间',
  PRIMARY KEY (`id`) USING BTREE
)DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;

